import {Component, Inject, NgModule} from '@angular/core';
import { HttpClient } from '@angular/common/http';
import {FormControl, FormGroup, Validators} from '@angular/forms';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})

export class HomeComponent {
  firstName = new FormControl('', Validators.required);
  lastName = new FormControl('', Validators.required);
  email = new FormControl('', Validators.required);
  valid = false;

  public onSubmit() {
    console.log('submit data');
    console.log(this.firstName)
  }
}
<form class="" (ngSubmit)="onSubmit()">
  <div class="row justify-content-center">
  <div class="col-6">
    <div class="form-group">
      <label for="firstName">First Name</label>
      <input type="text" class="form-control" id="firstName" placeholder="First Name" [formControl]="firstName" required>
      <div *ngIf="firstName.invalid && (firstName.dirty || firstName.touched)"
           class="alert alert-danger">

        <div *ngIf="firstName.errors?.['required']">
          Name is required.
        </div>
      </div>
    </div>
    <div class="form-group">
      <label for="lastName">Last Name</label>
      <input type="text" class="form-control" id="lastName" placeholder="Last Name" [formControl]="lastName" required>
    </div>
    <div class="form-group">
      <label for="emailInput">Email address</label>
      <input type="email" class="form-control" id="emailInput" placeholder="Enter email" [formControl]="email" required>
    </div>
    <button type="submit" class="btn btn-primary mt-3" (click)="onSubmit()" [disabled]="!valid">Submit</button>
  </div>
  </div>
</form>
