import {Component, Inject, NgModule} from '@angular/core';
import {HttpClient, HttpHeaders} from '@angular/common/http';
import {FormControl, FormGroup, Validators} from '@angular/forms';
import {getBaseUrl} from "../../main";

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})

export class HomeComponent {
  constructor(private http: HttpClient, @Inject('BASE_URL') baseUrl: string) { }

  firstName = new FormControl('', Validators.required);
  lastName = new FormControl('', Validators.required);
  email = new FormControl('', Validators.required);
  valid = false;

  public onSubmit() {
    const httpOptions = {
      headers: new HttpHeaders({'Content-Type': 'application/json'})
    }
    const payload = 'TestInput';
    console.log(JSON.stringify(payload))
    // this.http.put<any>(getBaseUrl() + 'form', JSON.stringify(payload), httpOptions).subscribe(result => {
    //   console.log(result);
    // })
  }
}
<form class="" (ngSubmit)="onSubmit()">
  <div class="row justify-content-center">
  <div class="col-6">
    <div class="form-group">
      <label for="firstName">First Name</label>
      <input type="text" class="form-control" id="firstName" placeholder="First Name" [formControl]="firstName" required>
      <div *ngIf="firstName.invalid && (firstName.dirty || firstName.touched)"
           class="alert alert-danger">

        <div *ngIf="firstName.errors?.['required']">
          First Name is required.
        </div>
      </div>
    </div>
    <div class="form-group">
      <label for="lastName">Last Name</label>
      <input type="text" class="form-control" id="lastName" placeholder="Last Name" [formControl]="lastName" required>
      <div *ngIf="lastName.invalid && (lastName.dirty || lastName.touched)"
           class="alert alert-danger">

        <div *ngIf="lastName.errors?.['required']">
          Last is required.
        </div>
      </div>
    </div>
    <div class="form-group">
      <label for="emailInput">Email address</label>
      <input type="email" class="form-control" id="emailInput" placeholder="Enter email" [formControl]="email" required>
      <div *ngIf="email.invalid && (email.dirty || email.touched)"
           class="alert alert-danger">

        <div *ngIf="email.errors?.['required']">
          Email is required.
        </div>
      </div>
    </div>
    <button type="submit" class="btn btn-primary mt-3" (click)="onSubmit()" >Submit</button>
  </div>
  </div>
</form>
